mapper.select:

select 

from TM_MAIL_KANRI
where CD_KS_JT_KYU = #{cdKsJtKyu,jdbcType=CHAR}
  and CD_KOSHINSHA_KAISHA = #{cdKoshinshaKaisha,jdbcType=CHAR}
  and CD_HOZON = #{cdHozon,jdbcType=CHAR}
  and CD_JT_SHIJI = #{cdJtShiji,jdbcType=CHAR}
  and CD_KS_JT_SHIN = #{cdKsJtShin,jdbcType=CHAR}
  and CD_ML_JUN = #{cdMlJun,jdbcType=CHAR}

